<script>
	import { _ } from 'svelte-i18n';
	import '../app.css';
	import { routes, url, handleAuthToken, tab, seaser } from '$lib/store/routes';
	import { browser } from '$app/environment';
	import { page } from '$app/stores';
	import { goto } from '$app/navigation';
	import { isLogin } from '$lib/store/profile';
	import { screen } from '$lib/store/screen';
	import { error_message } from '$lib/store/error-message';
	import { onMount } from 'svelte';
	import '../styles/global.css';
	import '../styles/navbar.css';
	import Navbar from '$lib/navbar.svelte';
	import Loader from '$lib/loader.svelte';
	import { ModeWatcher, mode } from 'mode-watcher';
	import { socketData } from '$lib/store/socket';
	import SocketManager from '$lib/socket/Socketmanager';
	import { ServerURl } from '$lib/backendUrl';
	$: url.set($page.url.pathname);

	$: urlString = $page.url.href;

	$: paramString = urlString.split('?')[1];

	$: queryString = new URLSearchParams(paramString);

	$: seaserEl = [];

	$: app_isLoading = true;

	function checkTheme(theme) {
		if (browser) {
			document.documentElement.setAttribute('data-theme', theme || 'light');
			document.documentElement.setAttribute('data-uniframe-theme', theme || 'light');
			if (theme === 'dark') document.body.classList.add('dark-theme');
			else document.body.classList.remove('dark-theme');
		}
	}
	mode.subscribe((theme) => {
		checkTheme(theme);
	});
	screen.subscribe((size) => {
		if (browser) {
			if (size <= 768) {
				document.body.classList.add('ui-is-w768');
			} else document.body.classList.remove('ui-is-w768');
		}
	});
	checkTheme($mode);
	$: {
		seaserEl = [];

		if (paramString) {
			for (let pair of queryString.entries()) {
				seaserEl.push(pair[1]);
			}
		}

		seaser.set(seaserEl);
		tab.set(seaser[0]);
	}

	let ens = browser && window.innerWidth;

	browser &&
		window.addEventListener('resize', () => {
			ens = window.innerWidth;
			screen.set(ens);
		});

	onMount(() => {
		ens = browser && window.innerWidth;
		screen.set(ens || 0);

		if (browser) {
			const io = SocketManager.socket(ServerURl());
			socketData.set({
				io,
				request: SocketManager.socketRequestBind(io)
			})
		}
	});

	$: {
		if (!$isLogin && $routes === '/profile') {
			goto('/login');
		}
	}
</script>
<ModeWatcher />
